int getCorrectedName(int curNodeName) {
	
	if (curNodeName < E_BONE_C_BEG) {
		if (curNodeName <= E_BONE_L_END) {
			return (
				curNodeName+(E_BONE_R_BEG-E_BONE_L_BEG)
			);
		}
		else {
			return (
				curNodeName-(E_BONE_R_BEG-E_BONE_L_BEG)
			);
		}
	}
	else {
		return curNodeName;
	}	
}

