/**
 * Creates a JSON encoded string for the value with all necessary characters escaped
 *
 * @access public
 *
 * @return std::string Returns the JSON string
 */
std::string JSONValue::Stringify(int curLev = 0) const
{
	std::string ret_string;
	
	switch (type)
	{
		case JSONType_Null:
			ret_string = "null";
			break;
		
		case JSONType_String:
			ret_string = StringifyString(string_value);
			break;
		
		case JSONType_Bool:
			ret_string = bool_value ? "true" : "false";
			break;
		
		case JSONType_Number:
		{
			if (isinf(number_value) || isnan(number_value))
				ret_string = "null";
			else
			{
				std::stringstream ss;
				ss.precision(15);
				ss << number_value;
				ret_string = ss.str();
			}
			break;
		}
		
		case JSONType_Array:
		{
			ret_string = "[";
			JSONArray::const_iterator iter = array_value.begin();
			while (iter != array_value.end())
			{
				//ret_string += SPACE_BUFFER[curLev];
				ret_string += (*iter)->Stringify(curLev + 1);
				
				// Not at the end - add a separator
				if (++iter != array_value.end()) {
					ret_string += ",";
				}
				else {
					//ret_string += "\n";
				}
			}
			ret_string += "]"; //SPACE_BUFFER[curLev]+
			break;
		}
		
		case JSONType_Object:
		{
			ret_string = "{\n";
			JSONObject::const_iterator iter = object_value.begin();
			while (iter != object_value.end())
			{
				ret_string += SPACE_BUFFER[curLev];
				ret_string += StringifyString((*iter).first);
				ret_string += ":";
				ret_string += (*iter).second->Stringify(curLev+1);
				
				// Not at the end - add a separator
				if (++iter != object_value.end()) {
					ret_string += ",\n";
				}
				else {
					ret_string += "\n";
				}
			}
			ret_string += SPACE_BUFFER[curLev]+"}";
			break;
		}
	}

	return ret_string;
}

