void DynBuffer::keyboardCB (unsigned char key, int x, int y)
        {
	    switch(key)
	    {
	    case 27: // ESCAPE
	        exit(0);
	        break;

	    case ' ':
	        if(pboSupported)
	            pboMode = ++pboMode % 3;
	        std::cout << "PBO mode: " << pboMode << std::endl;
	         break;

	    case 'd': // switch rendering modes (fill -> wire -> point)
	    case 'D':
	        drawMode = ++drawMode % 3;
	        if(drawMode == 0)        // fill mode
	        {
	            glPolygonMode(GL_FRONT_AND_BACK, GL_FILL);
	            glEnable(GL_DEPTH_TEST);
	            glEnable(GL_CULL_FACE);
	        }
	        else if(drawMode == 1)  // wireframe mode
	        {
	            glPolygonMode(GL_FRONT_AND_BACK, GL_LINE);
	            glDisable(GL_DEPTH_TEST);
	            glDisable(GL_CULL_FACE);
	        }
	        else                    // point mode
	        {
	            glPolygonMode(GL_FRONT_AND_BACK, GL_POINT);
	            glDisable(GL_DEPTH_TEST);
	            glDisable(GL_CULL_FACE);
	        }
	        break;

	    default:
	        ;
	    }
	}
void DynBuffer::mouseCB (int button, int state, int x, int y)
        {
	    mouseX = x;
	    mouseY = y;

	    if(button == GLUT_LEFT_BUTTON)
	    {
	        if(state == GLUT_DOWN)
	        {
	            mouseLeftDown = true;
	        }
	        else if(state == GLUT_UP)
	            mouseLeftDown = false;
	    }

	    else if(button == GLUT_RIGHT_BUTTON)
	    {
	        if(state == GLUT_DOWN)
	        {
	            mouseRightDown = true;
	        }
	        else if(state == GLUT_UP)
	            mouseRightDown = false;
	    }
	}
void DynBuffer::mouseMotionCB (int x, int y)
        {
	    if(mouseLeftDown)
	    {
	        cameraAngleY += (x - mouseX);
	        cameraAngleX += (y - mouseY);
	        mouseX = x;
	        mouseY = y;
	    }
	    if(mouseRightDown)
	    {
	        cameraDistance -= (y - mouseY) * 0.2f;
	        if(cameraDistance < 2.0f)
	            cameraDistance = 2.0f;

	        mouseY = y;
	    }
	}
