		// }
		
		//delete (singleton->compStack[curNodeId].data);
		//singleton->compStack[curNodeId].data = NULL;
		singleton->compStack[curNodeId].isValid = false;
		singleton->emptyStack.push_back(curNodeId);
		
		curNode->clearChildren();
		
