		if (isDraggingObject) {
			
			if (upInd == 0) {
				draggingToInd = 0;
				draggingToType = E_DT_NOTHING;
				performDrag(
				singleton->gameNetwork->isConnected,
					draggingFromInd,
					draggingFromType,
					draggingToInd,
					draggingToType,
					&(singleton->worldMarker)
				);
			}
			else {
				if (upInd >= E_OBJ_LENGTH) {
					
						if (isContainer[gameObjects[upInd].objectType]) {
							draggingToInd = upInd;
							draggingToType = E_DT_WORLD_OBJECT;
						}
						else {
							draggingToInd = 0;
							draggingToType = E_DT_NOTHING;
						}
						
						performDrag(
							singleton->gameNetwork->isConnected,
							draggingFromInd,
							draggingFromType,
							draggingToInd,
							draggingToType,
							&(singleton->worldMarker)
						);
				}						
			}
			
			
			
		}
