void GameEntManager::setSwing (float _mx, float _my, int actorId, int handNum, bool isKick)
          {
		
		float mx = _mx;
		float my = -_my;
		
		
		
		if (actorId < 0 ) {
			return;
		}
		
		//int handMod = 0;
		
		BaseObj* ca = &(gameObjects[actorId]);
		
		bool isHolding = (ca->isGrabbingId[handNum] > -1);
		
		// if (handNum == RLBN_RIGT) {
		// 	mx *= -1.0f;
		// }
		
		// if (handNum == RLBN_LEFT) {
		// 	handMod = 1;
		// }
		
		int curAttack = E_PG_SLSH;
		
		if (abs(my) > abs(mx)) {
			if (my > 0.0f) {
				// top
				
				if (isKick) {
					curAttack = E_PG_BKIK;
				}
				else {
					if (isHolding) {
						curAttack = E_PG_HACK;
					}
					else {
						curAttack = E_PG_UPPR;
					}
				}
				
				
			}
			else {
				// bottom
				
				if (isKick) {
					curAttack = E_PG_FRNT;
				}
				else {
					if (isHolding) {
						curAttack = E_PG_STAB;
					}
					else {
						curAttack = E_PG_JABP;
					}
				}
				
			}
		}
		else {
			if (mx > 0.0f) {
				// right
				
				if (isKick) {
					curAttack = E_PG_ROUN;
				}
				else {
					if (isHolding) {
						curAttack = E_PG_SLSH;
					}
					else {
						curAttack = E_PG_HOOK;
					}
				}
				
			}
			else {
				// left
				
				if (isKick) {
					curAttack = E_PG_REVR;
				}
				else {
					if (isHolding) {
						curAttack = E_PG_BACK;
					}
					else {
						curAttack = E_PG_ELBO;
					}
				}
				
			}
		}
		
		//curAttack += handMod;
		
		ca->swingType[handNum] = curAttack;
		
	}
void GameEntManager::nextSwing (int actorId, int handNum)
                                                 {
		
		if (actorId < 0 ) {
			return;
		}
		
		BaseObj* ca = &(gameObjects[actorId]);
		
		ca->swingType[handNum] += iGenRand2(1,10);
		
		if (ca->swingType[handNum] > (E_PG_FRNT)) {
			ca->swingType[handNum] -= E_PG_FRNT;
			ca->swingType[handNum] += (E_PG_SLSH);
		}
		
		
	}
