GamePageHolder * GameLogic::getHolderById (int blockId, int holderId)
                                                                 {
		
		if ((blockId < 0) || (holderId < 0)) {
			return NULL;
		}
		
		GameBlock* curBlock = singleton->gw->blockData[blockId];
		GamePageHolder* curHolder;
		if (curBlock != NULL) {
			curHolder = curBlock->holderData[holderId];
			return curHolder;
		}
		else {
			return NULL;
		}
	}
